pd_direct_emax <- function() {
  ini({
    lE0   <- log(10);  label("Baseline effect")
    lEmax <- log(50);  label("Maximum drug effect")
    lEC50 <- log(5);   label("Concentration for 50% effect (mg/L)")
    eta.E0   ~ 0.1
    eta.Emax ~ 0.1
    add.sd <- 2
  })
  model({
    E0   <- exp(lE0   + eta.E0)
    Emax <- exp(lEmax + eta.Emax)
    EC50 <- exp(lEC50)
    E = E0 + Emax * Cc / (EC50 + Cc)
    E ~ add(add.sd)
  })
}
