pd_direct_imax <- function() {
  ini({
    lE0   <- log(100); label("Baseline effect")
    lIC50 <- log(5);   label("Concentration for 50% inhibition (mg/L)")
    eta.E0 ~ 0.1
    add.sd <- 5
  })
  model({
    E0   <- exp(lE0 + eta.E0)
    IC50 <- exp(lIC50)
    E = E0 * (1 - Cc / (IC50 + Cc))
    E ~ add(add.sd)
  })
}
