pd_direct_imax_partial <- function() {
  ini({
    lE0   <- log(100);    label("Baseline effect")
    lImax <- logit(0.8);  label("Maximum fractional inhibition")
    lIC50 <- log(5);      label("Concentration for 50% inhibition (mg/L)")
    eta.E0 ~ 0.1
    add.sd <- 5
  })
  model({
    E0   <- exp(lE0 + eta.E0)
    Imax <- expit(lImax)
    IC50 <- exp(lIC50)
    E = E0 * (1 - Imax * Cc / (IC50 + Cc))
    E ~ add(add.sd)
  })
}
