pd_effect_cmt_imax <- function() {
  ini({
    lke0  <- log(0.5);  label("Effect compartment equilibration rate (1/h)")
    lE0   <- log(100);  label("Baseline effect")
    lIC50 <- log(10);   label("IC50 (mg/L)")
    eta.E0 ~ 0.1
    add.sd <- 5
  })
  model({
    ke0  <- exp(lke0)
    E0   <- exp(lE0 + eta.E0)
    IC50 <- exp(lIC50)
    d/dt(Ce) = ke0 * (Cc - Ce)
    E = E0 * (1 - Ce / (IC50 + Ce))
    E ~ add(add.sd)
  })
}
