pd_linear <- function() {
  ini({
    lE0    <- log(10); label("Baseline effect")
    lslope <- log(2);  label("Slope (effect/concentration)")
    eta.E0 ~ 0.1
    add.sd <- 2
  })
  model({
    E0    <- exp(lE0 + eta.E0)
    slope <- exp(lslope)
    E = E0 + slope * Cc
    E ~ add(add.sd)
  })
}
