pk_1cmt_oral_zero_order <- function() {
  ini({
    lTk  <- log(2);   label("Absorption duration (h)")
    lcl  <- log(5);   label("Clearance (L/h)")
    lv   <- log(50);  label("Volume of distribution (L)")
    eta.cl ~ 0.1
    eta.v  ~ 0.1
    prop.sd <- 0.2
  })
  model({
    Tk <- exp(lTk)
    cl <- exp(lcl + eta.cl)
    v  <- exp(lv  + eta.v)
    dur(depot) <- Tk
    d/dt(depot)   = -depot / Tk
    d/dt(central) =  depot / Tk - cl/v * central
    Cc = central / v
    Cc ~ prop(prop.sd)
  })
}
