pk_1cmt_oral <- function() {
  ini({
    lka  <- log(1);    label("Absorption rate constant (1/h)")
    lcl  <- log(5);    label("Clearance (L/h)")
    lv   <- log(50);   label("Volume of distribution (L)")
    eta.ka ~ 0.3
    eta.cl ~ 0.1
    eta.v  ~ 0.1
    prop.sd <- 0.2;    label("Proportional error")
  })
  model({
    ka <- exp(lka + eta.ka)
    cl <- exp(lcl + eta.cl)
    v  <- exp(lv  + eta.v)
    Cc <- linCmt()
    Cc ~ prop(prop.sd)
  })
}
